 76.5305C67.8913 77.1345 68.1023 77.7568 68.5503 78.1958L71.4493 81.0323H60.7445V81.0325ZM70.92 76.6559L74.0187 60.3716H75.4697L78.5685 76.6559L74.7441 80.3977L70.92 76.6559Z"/></svg>
          <animated-number class="stats_numbers" ref="statsT" :value="statsT" :formatValue="formatToPrice" :duration="duration"/>
          <span class="stats_txt">משתמשים</span>
        </div>
      </div>
</template>

<script>
  import AnimatedNumber from "animated-number-vue";
  export default {
    components: {
      AnimatedNumber
    },
    data: function () {
      return {
        statsArr : [0, 0, 0],
        statsF: 0,
        statsS: 0,
        statsT: 0,
        duration: 0,
      }
    },
    methods: {
      getNumbersFromAPI(){
        return axios.post('/api/getStatsNumbers')
        .then(response => {
          if(response.data.success){
            this.statsArr[0] = response.data.value.workers;
            this.statsArr[1] = response.data.value.orders;
            this.statsArr[2] = response.data.value.users;
          }
        });
      },
      formatToPrice(value) {
        return `${value.toFixed(0)}`;
      },
      checkCord(){
        let coordY =  this.$refs.stats.getBoundingClientRect().y ; // delete event listener
        if(coordY<650){
          this.duration = 1400;
          this.statsF =this.statsArr[0];
          this.statsS = this.statsArr[1];
          this.statsT = this.statsArr[2];
          document.removeEventListener('scroll', this.checkCord, false);
        }
      }
    },
    destroyed() {
      document.removeEventListener('scroll', this.checkCord, false);
    },
    mounted() {
      this.getNumbersFromAPI()
      .then(document.addEventListener('scroll',  this.checkCord));
    },
  };
</script>

